import {
  Routes,
  Route,
  useNavigationType,
  useLocation,
} from "react-router-dom";
import Locofy from "./pages/Locofy";
import { useEffect } from "react";

import firebase from 'firebase/app';
import 'firebase/auth';

const firebaseConfig = {
  apiKey: "AIzaSyBO8DhQvBEeKCAQ_Y3PZ7Fe8WpTupomPnE",
  authDomain: "internle.firebaseapp.com",
  projectId: "internle",
  storageBucket: "internle.appspot.com",
  messagingSenderId: "219631840743",
  appId: "1:219631840743:web:aecfbe9194cb8869f32142",
  measurementId: "G-C6BKREG41K"  
};

firebase.initializeApp(firebaseConfig);

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = "OPOC Login Page";
        metaDescription = "";
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Routes>
      <Route path="/" element={<Locofy />} />
    </Routes>
  );
}
export default App;
